import React, { useState, useEffect } from 'react'

const PomodoroDisplayScreen = () => {
  const [seconds, setSeconds] = useState(60)
  const [isActive, setIsActive] = useState(false)

  function toggle() {
    setIsActive(!isActive)
  }

  function reset() {
    setSeconds(0)
    setIsActive(false)
  }

  useEffect(() => {
    let timer = null
