    }
    return () => clearInterval(timer)
  }, [isActive, seconds])

  return (
    <>
      <h1>Display timer</h1>
      <p>Number of Pomodoros: 1</p>
      <p>Minutes: 25</p>
      <p>Seconds: {seconds}</p>
      <button onClick={toggle}>{isActive ? 'Pause' : 'Start'}</button>
      <button onClick={reset}>Reset timer</button>
    </>
  )
}

export default PomodoroDisplayScreen
