import React, { useState, useEffect } from 'react'

const PomodoroDisplayScreen = () => {
  let timeLeft = 10

  useEffect(() => {
    const timer = setTimeout(() => {
      timeLeft = timeLeft - 1
    }, 1000)
  })

  return (
    <>
      <h1>Display timer</h1>
      <p>Number of Pomodoros: 1</p>
      <p>Minutes: 25</p>
      <p>Seconds: {timeLeft}</p>
    </>
  )
}

export default PomodoroDisplayScreen
