import React, { useState, useEffect } from 'react'

const PomodoroDisplayScreen = () => {
  const [seconds, setSeconds] = useState(`${25 * 60}`)
  const [pomodoroDuration, setPomodoroDuration] = useState(25)
  const [restDuration, setRestDuration] = useState(5)
  const [isActive, setIsActive] = useState(false)

  const toggle = () => {
    setIsActive(!isActive)
  }

  const reset = () => {
    setSeconds(`${25 * 60}`)
    setIsActive(false)
  }
  const adjustPomodoro = () => {}
  const adjustRest = () => {}
  useEffect(() => {
    let timer = null
    if (isActive && seconds > 0) {
      timer = setInterval(() => {
        setSeconds((seconds) => seconds - 1)
      }, 1000)
    } else if (!isActive && seconds === 0) {
      clearInterval(timer)
    }
    return () => clearInterval(timer)
  }, [isActive, seconds])

  const style = {
    display: 'grid',
    placeItems: 'center',
  }
  return (
    <>
      <div style={style}>
        <h1>Display timer</h1>
        <p>Number of Pomodoros: 1</p>
        <p>
          {Math.trunc(seconds / 60)} : {seconds % 60}
        </p>
        <button onClick={toggle}>{isActive ? 'Pause' : 'Start'}</button>
        <p>
          Rest: {restDuration},{' '}
          <button
            onClick={() => {
              setRestDuration(restDuration - 1)
            }}
          >
            Decrease rest period
          </button>
          <button
            onClick={() => {
              setRestDuration(restDuration + 1)
            }}
          >
            Increase rest period
          </button>
        </p>
        <p>
          Duration of a pomodoro: {pomodoroDuration}{' '}
          <button
            onClick={() => {
              setPomodoroDuration(pomodoroDuration - 1)
            }}
          >
            Decrease rest period
          </button>
          <button
            onClick={() => {
              setPomodoroDuration(pomodoroDuration + 1)
            }}
          >
            Increase rest period
          </button>
        </p>
        <button onClick={reset}>Reset timer</button>
        <button onClick={adjustPomodoro}>Reset timer</button>
        <button onClick={adjustRest}>Reset timer</button>
        <button onClick={adjustPomodoro}>Reset timer</button>
      </div>
    </>
  )
}

export default PomodoroDisplayScreen
